

function ShowNoneTrend() {
    document.getElementById("year_trend").style.display = "none", document.getElementById("quarter_trend").style.display = "none", document.getElementById("month_trend").style.display = "none"
}
function ShowYTrend() {
    document.getElementById("year_trend").style.display = "block", document.getElementById("quarter_trend").style.display = "none", document.getElementById("month_trend").style.display = "none"
}

function ShowQTrend() {
    document.getElementById("year_trend").style.display = "none", document.getElementById("quarter_trend").style.display = "block", document.getElementById("month_trend").style.display = "none"
  }

function ShowMTrend() {
    document.getElementById("year_trend").style.display = "none", document.getElementById("quarter_trend").style.display = "none", document.getElementById("month_trend").style.display = "block"
  }
